import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import HomeIcon from '@mui/icons-material/Home';
import SettingsIcon from '@mui/icons-material/Settings';
import MenuIcon from '@mui/icons-material/Menu';
import './SideNav.css'; // Import the CSS file for styling

const SideNav = ({ darkMode }) => {
  const [visible, setVisible] = useState(false); // Set to false to make it non-visible by default

  const toggleNav = () => {
    setVisible(!visible);
  };

  return (
    <>
      <div className="menu-icon" onClick={toggleNav} style={{ position: 'fixed', top: '20px', left: '20px', zIndex: 1100, cursor: 'pointer' }}>
        <MenuIcon style={{ color: darkMode ? 'white' : 'black' }} />
      </div>
      <div className={`side-nav ${darkMode ? 'dark' : 'light'} ${visible ? 'visible' : ''}`}>
        <ul>
          <li>
            <Link to="/" onClick={toggleNav}>
              <HomeIcon className="icon" />
              <span className="text">Home</span>
            </Link>
          </li>
          <li>
            <Link to="/settings" onClick={toggleNav}>
              <SettingsIcon className="icon" />
              <span className="text">Settings</span>
            </Link>
          </li>
        </ul>
      </div>
    </>
  );
};

export default SideNav;
