import React from 'react';

const Settings = ({ darkMode, toggleDarkMode, language, setLanguage }) => {
  return (
    <div style={{ padding: '20px', color: darkMode ? 'white' : 'black' }}>
      <h2 style={{ color: darkMode ? 'white' : 'black' }}>Settings</h2>
      <div>
        <label style={{ color: darkMode ? 'white' : 'black' }}>
          <input 
            type="checkbox" 
            checked={darkMode} 
            onChange={toggleDarkMode} 
          />
          Dark Mode
        </label>
      </div>
      <div>
        <label style={{ color: darkMode ? 'white' : 'black' }}>
          Language:
          <select 
            value={language} 
            onChange={(e) => setLanguage(e.target.value)} 
            style={{ marginLeft: '10px', backgroundColor: darkMode ? '#333' : 'white', color: darkMode ? 'white' : 'black' }}
          >
            <option value="en">English</option>
            <option value="es">Spanish</option>
            <option value="fr">French</option>
            <option value="de">German</option>
          </select>
        </label>
      </div>
    </div>
  );
};

export default Settings;
